package chapter09;

public class InheritanceMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		StrawBerry obj=new StrawBerry();
		obj.Set1("Berry", "여름");
		obj.Set2("딸기", "중");
		obj.Set3("빨강", 7000);
		obj.Disp();
		System.out.println("-------------------");
		obj.Disp2();
		System.out.println("-------------------");
		obj.Disp3();
		System.out.println("-------------------");
		
	}

}
